function updateColor(){let e=document.getElementById("color1").value,t=document.getElementById("color2").value;document.getElementById("pick-background").style.backgroundImage=`-webkit-linear-gradient(50deg, ${e} 0, ${t} 100%)`}AUTH.do("check_auth",()=>{let e=JSON.parse(localStorage.getItem("user")).color;document.getElementById("nav-brand").style.backgroundColor=e;let t=document.getElementsByClassName("gradient");for(let l=0;l<t.length;l++)t[l].style.backgroundImage=`-webkit-linear-gradient(50deg, #ff4169 0, ${e} 100%)`});